mapper.delete:

DELETE FROM board
WHERE id = #{id}
  AND member_id = #{memberId}

mapper.update:

UPDATE board
   SET title = #{title},
       content = #{content},
       regdate = SYSDATE,
       secret_flag = #{secretFlag},
       secret_pw = #{secretPw}
 WHERE id = #{id}
   AND member_id = #{memberId}
